static jo_palette bg_palette;

jo_palette	*garfield_palette_handling(void)
{
    jo_create_palette(&bg_palette);
    return (&bg_palette);
}

void init_background(void) {
    jo_img_8bits    img;
    jo_set_tga_palette_handling(garfield_palette_handling);
    img.data = JO_NULL;
    jo_tga_8bits_loader(&img, "TEX", "GARFIELD.TGA", 0);
    jo_vdp2_set_nbg1_8bits_image(&img, bg_palette.id, false);
    jo_free_img(&img);

    slScrPosNbg1(toFIXED(0.0), toFIXED(0.0));
    slZoomNbg1(toFIXED(1.0), toFIXED(1.0));
}

