static jo_palette flower3_palette;
static jo_palette flower4_palette;

jo_palette	*my_flower1_palette_handling(void)
{
    jo_create_palette(&flower1_palette);
    return (&flower1_palette);
}

jo_palette	*my_flower2_palette_handling(void)
{
    jo_create_palette(&flower2_palette);
    return (&flower2_palette);
}
jo_palette	*my_flower3_palette_handling(void)
{
    jo_create_palette(&flower3_palette);
    return (&flower3_palette);
}
jo_palette	*my_flower4_palette_handling(void)
{
    jo_create_palette(&flower4_palette);
    return (&flower4_palette);
}

void init_flower_img(void) {
    // load same image 4 times so they can have unique palettes (fucking dumb!)
    jo_set_tga_palette_handling(my_flower1_palette_handling);
    flower1_spr_id = jo_sprite_add_tga(JO_ROOT_DIR, "FLOWER.TGA", 255);
    jo_set_tga_palette_handling(my_flower2_palette_handling);
    flower2_spr_id = jo_sprite_add_tga(JO_ROOT_DIR, "FLOWER.TGA", 255);
    jo_set_tga_palette_handling(my_flower3_palette_handling);
    flower3_spr_id = jo_sprite_add_tga(JO_ROOT_DIR, "FLOWER.TGA", 255);
    jo_set_tga_palette_handling(my_flower4_palette_handling);
    flower4_spr_id = jo_sprite_add_tga(JO_ROOT_DIR, "FLOWER.TGA", 255);
    
    InitNormal2d(&hslFlower, &rgbFlower, &light3, &p_rangeFlower1, &attrFlower1);
    InitNormal2d(&hslFlower, &rgbFlower, &light3, &p_rangeFlower2, &attrFlower2);
    MultiPaletteUpdate(&flower1_palette, &hslFlower, &hsl_incFlower0, &p_rangeFlower0); // set initial image colors
    
    InitNormal2d(&hslFlower1, &rgbFlower, &light3, &p_rangeFlower1, &attrFlower1);
    InitNormal2d(&hslFlower1, &rgbFlower, &light3, &p_rangeFlower2, &attrFlower4);
    MultiPaletteUpdate(&flower2_palette, &hslFlower1, &hsl_incFlower0, &p_rangeFlower0); // set initial image colors
    
    InitNormal2d(&hslFlower2, &rgbFlower, &light3, &p_rangeFlower1, &attrFlower1);
    InitNormal2d(&hslFlower2, &rgbFlower, &light3, &p_rangeFlower2, &attrFlower5);
    MultiPaletteUpdate(&flower3_palette, &hslFlower2, &hsl_incFlower0, &p_rangeFlower0); // set initial image colors
    
    InitNormal2d(&hslFlower3, &rgbFlower, &light3, &p_rangeFlower1, &attrFlower1);
    InitNormal2d(&hslFlower3, &rgbFlower, &light3, &p_rangeFlower2, &attrFlower6);
    MultiPaletteUpdate(&flower4_palette, &hslFlower3, &hsl_incFlower0, &p_rangeFlower0); // set initial image colors
}

void update_flower_color(void) {
    // flower1
    NormalMapLighting2d(&hslFlower, &rgbFlower, &bufferFlower, &light3, &p_rangeFlower1, &hsl_incFlower0); // center
    hsl_incFlower0.h += 1;
    NormalMapLighting2d(&hslFlower, &rgbFlower, &bufferFlower, &light3, &p_rangeFlower2, &hsl_incFlower0); // petals

    // flower2
    NormalMapLighting2d(&hslFlower1, &rgbFlower, &bufferFlower1, &light3, &p_rangeFlower1, &hsl_incFlower0); // center
    hsl_incFlower1.h += 1;
    NormalMapLighting2d(&hslFlower1, &rgbFlower, &bufferFlower1, &light3, &p_rangeFlower2, &hsl_incFlower1); // petals

    // flower3
    NormalMapLighting2d(&hslFlower2, &rgbFlower, &bufferFlower2, &light3, &p_rangeFlower1, &hsl_incFlower0); // center
    hsl_incFlower2.h += 1;
    NormalMapLighting2d(&hslFlower2, &rgbFlower, &bufferFlower2, &light3, &p_rangeFlower2, &hsl_incFlower2); // petals

    // flower4
    NormalMapLighting2d(&hslFlower3, &rgbFlower, &bufferFlower3, &light3, &p_rangeFlower1, &hsl_incFlower0); // center
    hsl_incFlower3.h += 1;
    NormalMapLighting2d(&hslFlower3, &rgbFlower, &bufferFlower3, &light3, &p_rangeFlower2, &hsl_incFlower3); // petals
}

void update_flower_palette(void) {
    UpdatePaletteFromBuffer(&bufferFlower, &flower1_palette, &p_rangeFlower0);
    UpdatePaletteFromBuffer(&bufferFlower1, &flower2_palette, &p_rangeFlower0);
    UpdatePaletteFromBuffer(&bufferFlower2, &flower3_palette, &p_rangeFlower0);
    UpdatePaletteFromBuffer(&bufferFlower3, &flower4_palette, &p_rangeFlower0);
}


